import React, { Component } from 'react';
import geoData from '../static/world-50m.json'
import {
  ComposableMap,
  ZoomableGlobe,
  Geographies,
  Geography,
  Graticule,
} from "react-simple-maps"


const mapStyles = {
  width: "100%",
  maxWidth: "100%",
  margin: "0 auto",
}

class MapView extends Component {
  state = {
    rotation: [0, 0],
  }

  startAnimation = () => {
    const rotation = [this.state.rotation[0] + 0.5, this.state.rotation[1] - 0.18]
    this.setState({ rotation })
  }

  componentDidMount() {
    this.autorotation = timer(this.startAnimation)
  }


  render() {
    return(
      <div>
        <ComposableMap
          width={500}
          height={500}
          projection="orthographic"
          projectionConfig={{ scale: 220 }}
          style={mapStyles}
        >
          <ZoomableGlobe center={this.props.mapCenter ? this.props.mapCenter.reverse() : [0,0]}>
            <circle cx={250} cy={250} r={220} fill="transparent" stroke="#CFD8DC" />
            <Geographies
              disableOptimization
              geography={geoData}
            >
              {(geos, proj) =>
                geos.map((geo, i) => (
                  <Geography
                    key={geo.id + i}
                    geography={geo}
                    projection={proj}
                    onClick={this.props.handleGeographyClick}
                    style={{
                      default: {
                        fill: "#637079",
                        stroke: "#CCD7DD",
                      },
                       hover: {
                         fill: "#CCD7DD",
                         stroke: "#637079"
                       },
                       pressed: {
                         fill: "#FFF",
                         stroke: "#000",
                         outline: "none",
                       }
                    }}
                  />
                ))
              }
            </Geographies>
            <Graticule globe={true} stroke={"#637079"} outline={false} step={[10, 10]} />
          </ZoomableGlobe>
        </ComposableMap>
      </div>
    )
  }
}



MapView.defaultProps = {
  center: [0, 0]
}

export default MapView
