export const fetchUserLocation = () => dispatch => {
  return fetch('https://api.ipgeolocation.io/ipgeo?apiKey=2b1627a69cc441feb63b250c1e0f79cf')
  .then(location => dispatch({type:'USER_LOCATION_FETCH_SUCCESS', payload: location}))  
}
