const initialState = {
  visits: [],
  visit: {},
  userLocation: {},
  userLocationLoaded: false
}

export default (state = initialState, action) => {
  switch (action.type) {
    case 'FETCH_VISITS_SUCCESS':
      return {...state, visits: action.payload}
    case 'ADD_VISIT_SUCCESS':
      return {...state, visit: action.payload}
    case 'USER_LOCATION_FETCH_SUCCESS':
      return {...state, userLocation: action.payload, userLocationLoaded: true}
    default:
      return state
  }
}
