import React, { Component } from 'react'
import { connect } from 'react-redux'
import UserLocation from '../components/UserLocation'
import { fetchUserLocation } from '../redux/actions/visitActions'


class LocationContainer extends Component {

  componentDidMount() {
    if (this.props.userLocation === undefined) {
      this.props.fetchUserLocation()
    }
  }

  render() {
    if (this.props.userLocation === undefined) {
      return <div><h4>User Location Loading</h4></div>
    } else {
      return(
        <>
          <UserLocation userLocation={this.props.userLocation} />
        </>
      )
    }
  }
}

const mapDispatchToProps = (dispatch) => {
  return({
    fetchUserLocation: () => dispatch(fetchUserLocation())
  })
}

const mapStateToProps = (state) => {
  return {
    userLocation: state.userLocation
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(LocationContainer)
