import React, { Component } from 'react'

class WikiView extends Component {
  state = {
    wikiExtract: "..."
  }

  extractContent = () => {
    debugger
    if (this.props.wikiInfo.query && this.props.wikiInfo.length > 0) {
      this.setState({
        wikiExtract: this.props.wikiInfo.query.pages[Object.keys(this.props.wikiInfo.query.pages)[0]].extract
      })
    }
  }

  render() {
    return (
      <>
      <h3>Wikipedia</h3>
      <div>{this.extractContent()}</div>
      </>
    )
  }
}

export default WikiView
