import React, { Component } from 'react';
import geoData from '../static/world-50m.json'
import {
  ComposableMap,
  ZoomableGlobe,
  Geographies,
  Geography,
  Graticule,
} from "react-simple-maps"
import { geoPolyhedralWaterman } from 'd3-geo-polygon'
import { geoPath } from "d3-geo"


const wrapperStyles = {
  width: "100%",
  maxWidth: 980,
  margin: "0 auto",
}

class MapView extends Component {

  constructor() {
    // this.handleCountryClick = this.handleCountryClick.bind(this)
  }


  // handleCountryClick(geography) {
  //   console.log(geography)
  //   const path = geoPath().projection(this.projection())
  //   const centroid = this.projection().invert(path.centroid(geography))
  //   this.setState({
  //     center: centroid,
  //     currentCountry: geography.properties.iso_a3
  //   })
  // }


  render() {
    return (
      <div style={wrapperStyles}>
        <ComposableMap
          projection="orthographic"
          projectionConfig={{
            scale: 300,
          }}
          width={980}
          height={980}
          style={{
            width: "100%",
            height: "auto",
          }}
          >
          <ZoomableGlobe center={[-20, 0]}>
            <Geographies geography={geoData}>
              {(geographies, projection) => geographies.map((geography, i) => geography.id !== "ATA" && (
                <Geography
                  key={i}
                  geography={geography}
                  projection={projection}
                  onClick={this.handleCountryClick}
                  style={{
                    default: {
                      fill: "#000",
                      stroke: "#FFF",
                      strokeWidth: 0.75,
                      outline: "none",
                    },
                    hover: {
                      fill: "#FFF",
                      stroke: "#000",
                      strokeWidth: 0.75,
                      outline: "none",
                    },
                    pressed: {
                      fill: "#FFF",
                      stroke: "#000",
                      strokeWidth: 0.75,
                      outline: "none",
                    },
                  }}
                />
              ))}
            </Geographies>
            <Graticule />
          </ZoomableGlobe>
        </ComposableMap>
      </div>
    )
  }
}

export default MapView
